import Hls from 'hls.js';

export default class PlaybackCoreV2 {
    constructor(videoElement, camId, baseUrl = '/api') {
        this.video = videoElement;
        this.camId = camId;
        this.baseUrl = baseUrl;
        this.hls = null;
        this.currentFragPDT = 0;
        this.currentFragStartPTS = 0;
        this.playlistStartTime = 0;
        this.playlistEndTime = 0;
    }

    setSegments(segments) { }

    start(startEpochMs) {
        if (this.hls) {
            this.hls.destroy();
            this.hls = null;
        }

        // OPTIMIZATION: Use smaller playlist window (2 hours instead of 12)
        // This prevents huge playlists that cause memory/performance issues
        const PLAYLIST_WINDOW_MS = 2 * 60 * 60 * 1000; // 2 hours

        this.playlistStartTime = startEpochMs;
        this.playlistEndTime = startEpochMs + PLAYLIST_WINDOW_MS;

        const playlistUrl = `${this.baseUrl}/playback/playlist/${this.camId}.m3u8?start=${this.playlistStartTime}&end=${this.playlistEndTime}`;

        if (Hls.isSupported()) {
            this.hls = new Hls({
                debug: false,
                enableWorker: true,
                manifestLoadingTimeOut: 10000,
                fragLoadingTimeOut: 20000,
                startFragPrefetch: true,

                // Buffer settings optimized for VOD
                maxBufferLength: 30,
                maxMaxBufferLength: 60,
                backBufferLength: 10,
            });

            this.hls.loadSource(playlistUrl);
            this.hls.attachMedia(this.video);

            this.hls.on(Hls.Events.MANIFEST_PARSED, (event, data) => {
                this.video.play().catch(e => console.warn("Autoplay blocked", e));
            });

            this.hls.on(Hls.Events.FRAG_CHANGED, (event, data) => {
                if (data.frag && data.frag.programDateTime) {
                    this.currentFragPDT = data.frag.programDateTime;
                    this.currentFragStartPTS = data.frag.start;

                    // AUTO-RELOAD: If we're approaching the end of the playlist window,
                    // reload with a new window starting from current position
                    const currentEpoch = this.getCurrentEpochMs();
                    const timeUntilEnd = this.playlistEndTime - currentEpoch;

                    // Reload when 30 minutes remain in the window
                    if (timeUntilEnd < 30 * 60 * 1000 && timeUntilEnd > 0) {
                        console.log('[PlaybackCore] Approaching end of playlist window, reloading...');
                        this.start(currentEpoch);
                    }
                }
            });

            this.hls.on(Hls.Events.ERROR, (event, data) => {
                if (data.fatal) {
                    console.error(`[HLS] FATAL ERROR: ${data.type}`, data.details);
                    switch (data.type) {
                        case Hls.ErrorTypes.NETWORK_ERROR:
                            console.log('[HLS] Network error, retrying...');
                            this.hls.startLoad();
                            break;
                        case Hls.ErrorTypes.MEDIA_ERROR:
                            console.log('[HLS] Media error, recovering...');
                            this.hls.recoverMediaError();
                            break;
                        default:
                            console.error('[HLS] Unrecoverable error, destroying player');
                            this.hls.destroy();
                            break;
                    }
                }
            });

        } else if (this.video.canPlayType('application/vnd.apple.mpegurl')) {
            this.video.src = playlistUrl;
            this.video.addEventListener('loadedmetadata', () => {
                this.video.play();
            });
        }
    }

    seekTo(ts) {
        this.start(ts);
    }

    getCurrentEpochMs() {
        if (this.currentFragPDT && this.hls) {
            const timeInFrag = this.video.currentTime - this.currentFragStartPTS;
            const absoluteTime = this.currentFragPDT + (timeInFrag * 1000);
            return absoluteTime;
        }
        if (this.video.getStartDate && typeof this.video.getStartDate === 'function') {
            const sd = this.video.getStartDate();
            if (sd && !isNaN(sd.getTime())) {
                return sd.getTime() + (this.video.currentTime * 1000);
            }
        }
        return 0;
    }

    destroy() {
        if (this.hls) {
            this.hls.destroy();
            this.hls = null;
        }
    }
}
