import React, { useEffect, useState, useRef, useLayoutEffect } from "react";
import { useSearchParams, useNavigate } from "react-router-dom";
import API from "../api";
import PlaybackController, { PlayerStates } from "../services/PlaybackController";
import { ArrowLeft, Play, Pause, ChevronLeft, ChevronRight, ZoomIn, ZoomOut, Calendar, Monitor, Maximize } from "../components/Icons";

// --- AZIGUARD THEME REPLICA ---
const THEME = {
    bg: "#f0f2f5", // Light grey background
    header: "#ffffff",
    headerBorder: "#e1e4e8",
    accent: "#0056b3", // Professional Blue
    accentHover: "#004494",
    text: "#333333",
    textLight: "#666666",
    timelineTrack: "#ffffff",
    timelineRuler: "#e0e0e0",
    segment: "#d32f2f", // Red recording blocks
    segmentBorder: "#b71c1c",
    playhead: "#2196f3", // Blue playhead
    panelBorder: "#dce1e6"
};

export default function PlaybackModern() {
    const [searchParams] = useSearchParams();
    const navigate = useNavigate();
    const camId = searchParams.get("camId");

    // --- STATE ---
    const [selectedDate, setSelectedDate] = useState(new Date().toISOString().split('T')[0]);
    const [playerState, setPlayerState] = useState(PlayerStates.IDLE);
    const [serverTimezone, setServerTimezone] = useState(null);

    // Timeline State
    const [currentTimeMs, setCurrentTimeMs] = useState(Date.now());
    const [viewStartMs, setViewStartMs] = useState(new Date().setHours(0, 0, 0, 0));
    const [zoomPxPerSec, setZoomPxPerSec] = useState(0.01);
    const [containerWidth, setContainerWidth] = useState(1000);
    const [segments, setSegments] = useState([]);
    const [isLoadingData, setIsLoadingData] = useState(false);

    const videoRef = useRef(null);
    const playerRef = useRef(null);
    const timelineRef = useRef(null);
    const dragRef = useRef({ isDragging: false, startX: 0, startViewTime: 0 });

    // --- DATA LOADING & SETUP ---
    useEffect(() => {
        API.get('/system/time').then(res => {
            if (res.data?.raw?.['Time zone']) setServerTimezone(res.data.raw['Time zone'].split(' ')[0]);
        }).catch(() => { });
    }, []);

    useEffect(() => {
        if (selectedDate) setViewStartMs(new Date(selectedDate).setHours(0, 0, 0, 0));
    }, [selectedDate]);

    // --- CONTROLLER INIT ---
    useEffect(() => {
        if (videoRef.current && camId) {
            playerRef.current = new PlaybackController(videoRef.current, camId, (s) => setPlayerState(s));
            return () => playerRef.current?.destroy();
        }
    }, [camId]);

    // --- RESIZE HANDLER ---
    useLayoutEffect(() => {
        const updateWidth = () => { if (timelineRef.current) setContainerWidth(timelineRef.current.clientWidth); };
        window.addEventListener('resize', updateWidth);
        updateWidth();
        return () => window.removeEventListener('resize', updateWidth);
    }, []);

    // --- LOAD SEGMENTS ---
    useEffect(() => {
        if (!camId || !selectedDate) return;
        const fetchData = async () => {
            setIsLoadingData(true);
            try {
                const res = await API.get(`/api/playback/timeline-day/${camId}/${selectedDate}`);
                const data = res.data;
                const segs = (data.segments || []).map(s => ({
                    start: Number(s.start_ts || s.startTs),
                    end: Number(s.end_ts || s.endTs),
                    file: s.file
                })).sort((a, b) => a.start - b.start);
                setSegments(segs);
                if (playerRef.current) playerRef.current.loadSegments(segs);

                // Fit Day
                if (containerWidth > 0) {
                    setViewStartMs(new Date(selectedDate).setHours(0, 0, 0, 0));
                    setZoomPxPerSec(containerWidth / 86400);
                }
            } catch (e) { console.error(e); } finally { setIsLoadingData(false); }
        };
        fetchData();
    }, [camId, selectedDate]);

    // --- SYNC LOOP ---
    useEffect(() => {
        const i = setInterval(() => {
            if (playerState === PlayerStates.PLAYING && playerRef.current) {
                const now = playerRef.current.getCurrentTime();
                if (Math.abs(now - currentTimeMs) > 200) setCurrentTimeMs(now);
            }
        }, 100);
        return () => clearInterval(i);
    }, [playerState, currentTimeMs]);

    // --- ACTIONS ---
    const fitDay = () => {
        setViewStartMs(new Date(selectedDate).setHours(0, 0, 0, 0));
        setZoomPxPerSec(containerWidth / 86400);
    };
    const zoomIn = () => {
        const newZoom = Math.min(200, zoomPxPerSec * 2);
        const center = viewStartMs + ((containerWidth / 2) / zoomPxPerSec) * 1000;
        setZoomPxPerSec(newZoom);
        setViewStartMs(center - ((containerWidth / 2) / newZoom) * 1000);
    };
    const zoomOut = () => {
        const newZoom = Math.max(containerWidth / 86400, zoomPxPerSec / 2);
        const center = viewStartMs + ((containerWidth / 2) / zoomPxPerSec) * 1000;
        setZoomPxPerSec(newZoom);
        setViewStartMs(center - ((containerWidth / 2) / newZoom) * 1000);
    };
    const performSeek = (ts) => {
        setCurrentTimeMs(ts);
        playerRef.current?.seekTo(ts);
    };

    // --- EVENTS ---
    const handleMouseDown = (e) => {
        dragRef.current = { isDragging: true, startX: e.clientX, startView: viewStartMs };
        document.body.style.cursor = 'grabbing';
    };
    const handleMouseMove = (e) => {
        if (!dragRef.current.isDragging) return;
        const dt = ((dragRef.current.startX - e.clientX) / zoomPxPerSec) * 1000;
        setViewStartMs(dragRef.current.startView + dt);
    };
    const handleMouseUp = (e) => {
        if (dragRef.current.isDragging) {
            dragRef.current.isDragging = false;
            document.body.style.cursor = 'default';
            if (Math.abs(e.clientX - dragRef.current.startX) < 5) {
                const rect = timelineRef.current.getBoundingClientRect();
                const ts = viewStartMs + ((e.clientX - rect.left) / zoomPxPerSec) * 1000;
                performSeek(ts);
            }
        }
    };
    const handleWheel = (e) => {
        if (e.target.closest('.timeline-area')) {
            e.preventDefault();
            const rect = timelineRef.current.getBoundingClientRect();
            const mouseX = e.clientX - rect.left;
            const mouseTime = viewStartMs + (mouseX / zoomPxPerSec) * 1000;
            const factor = e.deltaY > 0 ? 0.9 : 1.1;
            const newZoom = Math.max(containerWidth / 86400, Math.min(200, zoomPxPerSec * factor));
            setZoomPxPerSec(newZoom);
            setViewStartMs(mouseTime - (mouseX / newZoom) * 1000);
        }
    };

    // --- RENDER HELPERS ---
    const timeToPx = (t) => (t - viewStartMs) / 1000 * zoomPxPerSec;

    const renderTicks = () => {
        const ticks = [];
        const pxPerMin = zoomPxPerSec * 60;
        let stepMin = 60 * 24;
        if (pxPerMin > 1000) stepMin = 1; else if (pxPerMin > 200) stepMin = 5; else if (pxPerMin > 50) stepMin = 15; else if (pxPerMin > 10) stepMin = 60; else stepMin = 240;

        const stepMs = stepMin * 60 * 1000;
        if (stepMs <= 0) return [];
        const start = Math.floor(viewStartMs / stepMs) * stepMs;
        const end = viewStartMs + (containerWidth / zoomPxPerSec * 1000);

        for (let t = start; t < end; t += stepMs) {
            const x = timeToPx(t);
            const date = new Date(t);
            const isHour = date.getMinutes() === 0;
            ticks.push(
                <div key={t} style={{ position: 'absolute', left: x, height: '100%', borderLeft: `1px solid ${isHour ? '#999' : '#ddd'}`, pointerEvents: 'none' }}>
                    {isHour && <div style={{ position: 'absolute', top: 4, left: 4, fontSize: 11, fontWeight: 600, color: '#333' }}>
                        {date.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })}
                    </div>}
                </div>
            );
        }
        return ticks;
    };

    return (
        <div style={{ display: 'flex', flexDirection: 'column', height: '100vh', background: THEME.bg, color: THEME.text, fontFamily: '-apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, Helvetica, Arial, sans-serif' }}>

            {/* 1. TOP NAVIGATION BAR */}
            <header style={{ height: 48, background: '#fff', borderBottom: `1px solid ${THEME.panelBorder}`, display: 'flex', alignItems: 'center', padding: '0 8px' }}>
                <div style={{ fontWeight: 800, fontSize: 18, color: '#0277bd', marginRight: 20 }}>AZIGUARD</div>

                <div style={{ display: 'flex', gap: 4 }}>
                    {['Live', 'Playback', 'Settings', 'AI Events'].map(tab => (
                        <div key={tab} style={{
                            padding: '6px 12px',
                            borderRadius: 4,
                            cursor: 'pointer',
                            fontSize: 13,
                            fontWeight: 500,
                            background: tab === 'Playback' ? '#e3f2fd' : 'transparent',
                            color: tab === 'Playback' ? '#0277bd' : '#666'
                        }} onClick={() => tab === 'Live' && navigate('/')}>
                            {tab}
                        </div>
                    ))}
                </div>

                <div style={{ flex: 1 }} />

                <div style={{ display: 'flex', alignItems: 'center', gap: 10, fontSize: 13, color: '#555' }}>
                    <span>{serverTimezone}</span>
                    <input type="date" value={selectedDate} onChange={e => setSelectedDate(e.target.value)}
                        style={{ border: `1px solid ${THEME.panelBorder}`, padding: '4px 8px', borderRadius: 4, color: '#333' }} />
                </div>
            </header>

            {/* 2. SUB-BAR (Cameras) */}
            <div style={{ height: 32, background: '#f8f9fa', borderBottom: `1px solid ${THEME.panelBorder}`, display: 'flex', alignItems: 'center', padding: '0 8px', overflowX: 'auto' }}>
                <div style={{ fontSize: 12, fontWeight: 600, color: '#444', marginRight: 10 }}>CAMERAS:</div>
                <div style={{ padding: '2px 8px', background: '#fff', border: '1px solid #ccc', borderRadius: 3, fontSize: 11, color: '#333' }}>{camId || 'Standard Camera'}</div>
            </div>

            {/* 3. MAIN VIDEO AREA */}
            <div style={{ flex: 1, background: '#e0e0e0', position: 'relative', display: 'flex', alignItems: 'center', justifyContent: 'center' }}>
                <div style={{ maxWidth: '95%', maxHeight: '95%', width: '100%', height: '100%', display: 'flex', justifyContent: 'center', boxShadow: '0 4px 12px rgba(0,0,0,0.1)' }}>
                    <video ref={videoRef} style={{ width: '100%', height: '100%', background: '#000' }} onClick={() => playerRef.current?.play()} />
                </div>

                {isLoadingData && <div style={overlayStyle}>Loading Timeline...</div>}
                {playerState === PlayerStates.BUFFERING && <div style={overlayStyle}>Buffering...</div>}
                {!isLoadingData && segments.length === 0 && <div style={{ ...overlayStyle, top: '40%' }}>No Recordings Found</div>}
            </div>

            {/* 4. FOOTER CONTROLS & TIMELINE */}
            <div style={{ background: '#fff', borderTop: `1px solid ${THEME.panelBorder}`, display: 'flex', flexDirection: 'column' }}>

                {/* TOOLBAR */}
                <div style={{ height: 40, borderBottom: `1px solid ${THEME.panelBorder}`, display: 'flex', alignItems: 'center', padding: '0 16px', gap: 16 }}>
                    <button onClick={() => playerRef.current?.seekTo(currentTimeMs - 10000)} style={ctrlBtn}>⏪ 10s</button>
                    <button onClick={() => playerState === PlayerStates.PLAYING ? playerRef.current?.pause() : playerRef.current?.play()}
                        style={{ ...ctrlBtn, color: THEME.accent, fontWeight: 'bold' }}>
                        {playerState === PlayerStates.PLAYING ? "PAUSE" : "PLAY"}
                    </button>
                    <button onClick={() => playerRef.current?.seekTo(currentTimeMs + 10000)} style={ctrlBtn}>10s ⏩</button>

                    <div style={{ width: 1, height: 20, background: '#ddd' }} />

                    <button onClick={zoomOut} style={ctrlBtn}>-</button>
                    <button onClick={fitDay} style={ctrlBtn}>FIT</button>
                    <button onClick={zoomIn} style={ctrlBtn}>+</button>

                    <div style={{ flex: 1 }} />
                    <div style={{ fontFamily: 'monospace', fontSize: 16, fontWeight: 600, color: '#333' }}>
                        {new Date(currentTimeMs).toLocaleTimeString()}
                    </div>
                </div>

                {/* TIMELINE TRACK */}
                <div className="timeline-area"
                    ref={timelineRef}
                    style={{ height: 80, background: THEME.timelineTrack, position: 'relative', overflow: 'hidden', cursor: dragRef.current.isDragging ? 'grabbing' : 'grab' }}
                    onMouseUp={handleMouseUp} onMouseLeave={handleMouseUp} onMouseMove={handleMouseMove} onMouseDown={handleMouseDown} onWheel={handleWheel}
                >
                    {renderTicks()}
                    {segments.map((s, i) => {
                        const x = timeToPx(s.start);
                        const w = Math.max(1, (s.end - s.start) / 1000 * zoomPxPerSec);
                        if (x + w < 0 || x > containerWidth) return null;
                        return (
                            <div key={i} style={{
                                position: 'absolute', left: x, width: w, top: 40, height: 20,
                                background: THEME.segment, borderRadius: 1
                            }} />
                        );
                    })}

                    {/* PLAYHEAD */}
                    <div style={{ position: 'absolute', left: timeToPx(currentTimeMs), top: 0, bottom: 0, width: 2, background: THEME.playhead, zIndex: 10, pointerEvents: 'none' }}>
                        <div style={{ position: 'absolute', top: 0, left: -6, borderLeft: '7px solid transparent', borderRight: '7px solid transparent', borderTop: `8px solid ${THEME.playhead}` }} />
                    </div>
                </div>
            </div>
        </div>
    );
}

const ctrlBtn = { border: '1px solid #ddd', background: '#f9f9f9', borderRadius: 4, padding: '4px 12px', cursor: 'pointer', color: '#444', fontSize: 12, fontWeight: 500 };
const overlayStyle = { position: 'absolute', background: 'rgba(255,255,255,0.9)', color: '#333', padding: '10px 20px', borderRadius: 8, fontSize: 14, fontWeight: 'bold', boxShadow: '0 2px 10px rgba(0,0,0,0.2)' };
