import React, { useEffect, useState, useRef, useCallback } from "react";
import { useSearchParams } from "react-router-dom";
import API from "../api";
import Hls from 'hls.js';

/* STYLES */
const styles = `
* { box-sizing: border-box; margin: 0; padding: 0; }
body { background: #1a1a1a; color: #ccc; font-family: 'Segoe UI', Tahoma, Geneva, Verdana, sans-serif; }
.playback-page { height: 100vh; display: flex; flex-direction: column; background: #1a1a1a; }

/* HEADER */
.playback-header { 
    height: 48px; background: #111; border-bottom: 1px solid #333; 
    display: flex; align-items: center; padding: 0 16px; gap: 16px; 
}
.header-title { font-size: 14px; font-weight: 600; color: #fff; margin-right: auto; }
.date-selector { 
    background: #222; border: 1px solid #444; color: #fff; 
    padding: 4px 12px; border-radius: 4px; cursor: pointer; font-size: 13px;
    display: flex; align-items: center; gap: 8px;
}
.date-selector:hover { background: #333; border-color: #666; }

/* CALENDAR POPUP */
.calendar-popup {
    position: absolute; top: 50px; left: 50%; transform: translateX(-50%);
    width: 280px; background: #222; border: 1px solid #555; border-radius: 6px;
    box-shadow: 0 10px 30px rgba(0,0,0,0.5); z-index: 2000;
    padding: 10px;
}
.cal-header { display: flex; justify-content: space-between; margin-bottom: 10px; color: #fff; }
.cal-grid { display: grid; grid-template-columns: repeat(7, 1fr); gap: 4px; }
.cal-day { 
    height: 30px; display: flex; align-items: center; justify-content: center; 
    font-size: 12px; border-radius: 3px; cursor: default; color: #555;
}
.cal-day.has-data { 
    color: #4da6ff; font-weight: bold; cursor: pointer; background: rgba(77, 166, 255, 0.1); 
}
.cal-day.has-data:hover { background: #4da6ff; color: #000; }
.cal-day.selected { background: #fff; color: #000; border: 1px solid #4da6ff; }
.cal-btn { background: none; border: none; color: #aaa; cursor: pointer; }
.cal-btn:hover { color: #fff; }

/* VIDEO */
.video-viewport { flex: 1; background: #000; position: relative; display:flex; align-items:center; justify-content:center; }
video { width: 100%; height: 100%; object-fit: contain; }

/* CONTROLS & TIMELINE */
.timeline-area { height: 100px; background: #0f0f0f; border-top: 1px solid #333; position: relative; user-select: none; cursor: crosshair; }
.time-ticks { position: absolute; top: 0; width: 100%; height: 20px; display: flex; pointer-events: none; }
.tick { flex: 1; border-left: 1px solid #333; font-size: 10px; color: #666; padding-left: 2px; }
.segments-track { position: absolute; top: 25px; bottom: 0; width: 100%; background: rgba(255,255,255,0.01); }
.segment { position: absolute; top: 5px; height: 30px; background: #1f9d55; min-width: 2px; opacity: 0.9; }
.playhead { position: absolute; top: 0; bottom: 0; width: 2px; background: #f00; z-index: 50; pointer-events: none; }
.controls { height: 40px; background: #111; display: flex; align-items: center; gap: 10px; padding: 0 10px; border-top: 1px solid #222; }
.btn { background: #333; color: #eee; border: 1px solid #444; padding: 4px 12px; cursor: pointer; font-size: 12px; }
.btn:hover { background: #444; }
.btn-primary { background: #0055aa; border-color: #004488; }
.backdrop { position: fixed; top: 0; left: 0; width: 100%; height: 100%; z-index: 1000; }
`;

export default function Playback() {
    const [params] = useSearchParams();
    const camId = params.get("camId");

    const [selectedDate, setSelectedDate] = useState(() => {
        const d = new Date();
        const offset = d.getTimezoneOffset() * 60000;
        return new Date(d.getTime() - offset).toISOString().split('T')[0];
    });

    const [timelineSegments, setTimelineSegments] = useState([]);
    const [serverDayStart, setServerDayStart] = useState(0);
    const [isPlaying, setIsPlaying] = useState(false);

    // Calendar State
    const [showCal, setShowCal] = useState(false);
    const [calYear, setCalYear] = useState(new Date().getFullYear());
    const [calMonth, setCalMonth] = useState(new Date().getMonth() + 1);
    const [daysWithData, setDaysWithData] = useState([]);

    const videoRef = useRef(null);
    const hlsRef = useRef(null);
    const cursorRef = useRef(null);
    const timelineRef = useRef(null);

    // FETCH CALENDAR DATA
    useEffect(() => {
        if (!showCal || !camId) return;
        API.get(`/playback/calendar-month/${camId}/${calYear}/${calMonth}?_ts=${Date.now()}`)
            .then(res => setDaysWithData(res.data.days || []))
            .catch(console.error);
    }, [showCal, calYear, calMonth, camId]);

    // FETCH TIMELINE
    useEffect(() => {
        if (!camId || !selectedDate) return;
        console.log(`Fetching timeline: ${selectedDate}`);
        API.get(`/playback/timeline-day/${camId}/${selectedDate}?_ts=${Date.now()}`)
            .then(res => {
                const { segments: raw, dayStart } = res.data;
                setServerDayStart(dayStart);
                const dayEnd = dayStart + 86400000;

                const norm = raw.map(e => {
                    const s = Math.max(Number(e.start_ts), dayStart);
                    const end = Math.min(Number(e.end_ts), dayEnd);
                    if (end <= s) return null;
                    return { left: (s - dayStart) / 86400000, width: (end - s) / 86400000, ts: Number(e.start_ts) };
                }).filter(Boolean);

                setTimelineSegments(norm);
            })
            .catch(console.error);
    }, [camId, selectedDate]);

    // PLAYBACK LOGIC (DIRECT FRAGMENTED MP4)
    const play = useCallback((startTime) => {
        if (!startTime) return;

        // Stop current if any
        if (videoRef.current) {
            videoRef.current.pause();

            // Direct Stream URL
            // Using _t to prevent caching
            const streamUrl = `${API.defaults.baseURL}/playback/stream/${camId}?start=${startTime}&_t=${Date.now()}`;
            console.log(`[Playback] Loading: ${streamUrl}`);

            videoRef.current.src = streamUrl;
            videoRef.current.load();

            // Auto-play
            videoRef.current.play().then(() => setIsPlaying(true)).catch(e => {
                console.error("Autoplay prevent:", e);
                // Muted autoplay fallback?
            });
        }
    }, [camId]);

    const stop = () => {
        if (videoRef.current) {
            videoRef.current.pause();
            videoRef.current.removeAttribute('src');
            videoRef.current.load();
        }
        setIsPlaying(false);
    };

    // DRAW TIMELINE (USER FIXED LOGIC)
    useEffect(() => {
        const canvas = timelineRef.current;
        if (!canvas || !serverDayStart) return;
        const ctx = canvas.getContext('2d');
        const width = canvas.width;
        const height = canvas.height;

        // Clear
        ctx.clearRect(0, 0, width, height);

        // Background
        ctx.fillStyle = '#1e1e1e';
        ctx.fillRect(0, 0, width, height);

        // Hours
        ctx.strokeStyle = '#444';
        ctx.lineWidth = 1;
        ctx.beginPath();
        for (let h = 0; h <= 24; h++) {
            const x = (h / 24) * width;
            ctx.moveTo(x, 0);
            ctx.lineTo(x, height);
            // Label
            ctx.fillStyle = '#888';
            ctx.fillText(`${h}:00`, x + 2, 12);
        }
        ctx.stroke();

        // SEGMENTS (TRASSIR STYLE)
        if (timelineSegments && timelineSegments.length > 0) {
            const DAY_MS = 86400000;
            const pxPerSec = width / (24 * 3600); // pixels per second

            // 1. FILTER & NORMALIZE
            const daySegments = timelineSegments.map(s => {
                // Ensure TS are numbers
                const sStart = Number(s.start_ts);
                const sEndRaw = Number(s.end_ts);
                // Handle open-ended segments (0) -> until now or end of day
                const sEnd = sEndRaw === 0 ? Math.min(Date.now(), serverDayStart + DAY_MS) : sEndRaw;

                const start = Math.max(sStart, serverDayStart);
                const end = Math.min(sEnd, serverDayStart + DAY_MS);

                if (end <= start) return null;

                return {
                    startSec: (start - serverDayStart) / 1000,
                    endSec: (end - serverDayStart) / 1000
                };
            }).filter(Boolean);

            // 2. DRAW
            ctx.fillStyle = '#00ff00'; // Green Bar
            daySegments.forEach(s => {
                const x = s.startSec * pxPerSec;
                const w = Math.max((s.endSec - s.startSec) * pxPerSec, 2); // Min 2px

                // Draw in bottom half
                ctx.fillRect(x, height / 2, w, height / 2);
            });
        }

        // CURSOR (If Playing)
        // We can draw cursor here or use DOM element. 
        // Logic kept in DOM element for smooth updates, so no change needed here for cursor.

    }, [timelineSegments, serverDayStart]);

    // SEEK HANDLER (ABSOLUTE)
    const handleSeek = (e) => {
        if (!serverDayStart) return;
        const rect = timelineRef.current.getBoundingClientRect();
        const width = rect.width;
        const x = e.clientX - rect.left;

        // Ratio 0..1
        const ratio = Math.max(0, Math.min(1, x / width));

        // Absolute Timestamp
        const clickedMs = Math.floor(serverDayStart + ratio * 86400000);

        console.log(`[Timeline] Seek to ${new Date(clickedMs).toLocaleTimeString()} (${clickedMs})`);

        // Move Cursor Visual
        if (cursorRef.current) cursorRef.current.style.left = (ratio * 100) + "%";

        play(clickedMs);
    };


    // CALENDAR RENDER
    const renderCalendar = () => {
        const daysInMonth = new Date(calYear, calMonth, 0).getDate();
        const firstDay = new Date(calYear, calMonth - 1, 1).getDay(); // 0(Sun)..6(Sat)
        const offset = firstDay === 0 ? 6 : firstDay - 1; // Mon=0

        const grid = [];
        for (let i = 0; i < offset; i++) grid.push(<div key={`empty-${i}`} />);

        for (let d = 1; d <= daysInMonth; d++) {
            const hasData = daysWithData.includes(d);
            const isSelected = (d === new Date(selectedDate).getDate()) &&
                (calMonth === new Date(selectedDate).getMonth() + 1);

            grid.push(
                <div key={d}
                    className={`cal-day ${hasData ? 'has-data' : ''} ${isSelected ? 'selected' : ''}`}
                    onClick={() => {
                        if (hasData) {
                            const m = String(calMonth).padStart(2, '0');
                            const day = String(d).padStart(2, '0');
                            setSelectedDate(`${calYear}-${m}-${day}`);
                            setShowCal(false);
                        }
                    }}
                >
                    {d}
                </div>
            );
        }
        return grid;
    };

    return (
        <div className="playback-page">
            <style>{styles}</style>
            {showCal && <div className="backdrop" onClick={() => setShowCal(false)} />}

            <div className="playback-header">
                <button className="btn" onClick={() => window.location.href = '/'}>‚Üê Back</button>
                <div className="header-title">{camId}</div>

                <div className="date-selector" onClick={() => setShowCal(!showCal)}>
                    üìÖ {selectedDate}
                </div>

                {showCal && (
                    <div className="calendar-popup">
                        <div className="cal-header">
                            <button className="cal-btn" onClick={() => setCalMonth(m => m === 1 ? 12 : m - 1)}>‚óÄ</button>
                            <span>{calYear} - {calMonth}</span>
                            <button className="cal-btn" onClick={() => setCalMonth(m => m === 12 ? 1 : m + 1)}>‚ñ∂</button>
                        </div>
                        <div className="cal-grid">
                            {['L', 'M', 'M', 'J', 'V', 'S', 'D'].map(d => <div key={d} style={{ textAlign: 'center', fontSize: 10, color: '#666' }}>{d}</div>)}
                            {renderCalendar()}
                        </div>
                    </div>
                )}
            </div>

            <div className="video-viewport">
                <video ref={videoRef} controls autoPlay muted playsInline />
            </div>

            <div className="controls">
                <button className="btn btn-primary" onClick={() => timelineSegments[0] && play(timelineSegments[0].ts)}>Play All</button>
                <button className="btn" onClick={stop}>Stop</button>
            </div>

            <div className="timeline-area" ref={timelineRef} onClick={handleSeek}>
                <div className="time-ticks">
                    {Array.from({ length: 24 }, (_, i) => i).map(h => <div key={h} className="tick">{h}</div>)}
                </div>
                <div className="segments-track">
                    {timelineSegments.map((s, i) => (
                        <div key={i} className="segment" style={{ left: `${s.left * 100}%`, width: `${s.width * 100}%` }} />
                    ))}
                </div>
                <div ref={cursorRef} className="playhead" style={{ left: '0%' }} />
            </div>
        </div>
    );
}
