export default class PlaybackCoreV2 {
    constructor(videoElement, camId, baseUrl = '/api') {
        this.video = videoElement;
        this.camId = camId;
        this.baseUrl = baseUrl;

        this.mediaSource = null;
        this.sourceBuffer = null;

        // OPTIMIZATION: LARGER SEGMENTS = FEWER HTTP REQUESTS = LESS OVERHEAD
        this.SEGMENT_SEC = 6;
        this.TARGET_BUFFER = 30; // Increased buffer target

        this.nextTs = null;
        this.isFetching = false;
        this.started = false;

        this.isSeeking = false;
        this.seekTargetTs = null;

        this.checkInterval = null;
        this.debugInterval = null;
        this.abortController = null;

        this.gapRetries = 0;
        this.MAX_GAP_RETRIES = 15;
    }

    start(startEpochMs) {
        console.log(`[PlaybackCore] Starting at ${new Date(startEpochMs).toISOString()}`);
        this.nextTs = startEpochMs;
        this.isSeeking = false;
        this.started = false;
        this.gapRetries = 0;

        this.mediaSource = new MediaSource();
        this.video.src = URL.createObjectURL(this.mediaSource);

        this.mediaSource.addEventListener('sourceopen', this.onSourceOpen.bind(this));

        if (this.checkInterval) clearInterval(this.checkInterval);
        this.checkInterval = setInterval(this.ensureBuffer.bind(this), 200);

        // Start playing ONLY when we have data - wait for first append

        this.startDebugLogger();
    }

    destroy() {
        if (this.checkInterval) clearInterval(this.checkInterval);
        if (this.debugInterval) clearInterval(this.debugInterval);
        if (this.abortController) this.abortController.abort();
        if (this.video) {
            this.video.pause();
            this.video.removeAttribute('src');
            this.video.load();
        }
        this.mediaSource = null;
        this.sourceBuffer = null;
    }

    onSourceOpen() {
        if (this.sourceBuffer || this.mediaSource.readyState !== 'open') return;
        try {
            this.sourceBuffer = this.mediaSource.addSourceBuffer('video/mp4; codecs="avc1.64001f"');
            this.sourceBuffer.mode = 'segments';
        } catch (e) {
            console.warn("[MSE] High Profile rejected, trying Main Profile...", e);
            try {
                this.sourceBuffer = this.mediaSource.addSourceBuffer('video/mp4; codecs="avc1.4d401e"');
                this.sourceBuffer.mode = 'segments';
            } catch (e2) {
                console.error("[MSE] Fatal Codec Error:", e2);
            }
        }
    }

    async ensureBuffer() {
        if (this.isSeeking) return;
        if (this.gapRetries > this.MAX_GAP_RETRIES) return;

        if (!this.sourceBuffer || this.sourceBuffer.updating) return;
        if (this.isFetching || this.nextTs === null) return;
        if (this.mediaSource.readyState !== 'open') return;

        const current = this.video.currentTime || 0;
        const bufferedEnd = this.getBufferedEnd();

        if (!this.started || bufferedEnd - current < this.TARGET_BUFFER) {
            this.isFetching = true;
            await this.fetchAndAppend(this.nextTs);
            this.isFetching = false;
        }
    }

    async fetchAndAppend(ts) {
        if (this.isSeeking) return;

        const url = `${this.baseUrl}/playback/segment/${this.camId}?ts=${ts}&dur=${this.SEGMENT_SEC}`;

        try {
            this.abortController = new AbortController();

            // Timeout fetch to avoid hanging if backend is slow
            const timeoutId = setTimeout(() => this.abortController.abort(), 10000); // 10s max per segment

            const res = await fetch(url, { signal: this.abortController.signal });
            clearTimeout(timeoutId);

            if (!res.ok) {
                if (res.status === 404) await this.skipGap();
                return;
            }

            const buf = await res.arrayBuffer();
            if (this.isSeeking) return;

            await this.appendBuffer(buf);
            this.gapRetries = 0;

            // SMART START: Play only after buffer > 2s to prevent immediate stall
            if (!this.started && !this.isSeeking) {
                const buffEnd = this.getBufferedEnd();
                if (buffEnd > 0.5) { // Just ensure we have SOME data
                    try { await this.video.play(); this.started = true; } catch (e) { }
                }
            }

            this.nextTs += (this.SEGMENT_SEC * 1000);

        } catch (e) {
            if (e.name !== 'AbortError') console.error("[MSE] Fetch Error:", e);
        } finally {
            this.abortController = null;
        }
    }

    async skipGap() {
        this.gapRetries++;
        if (this.gapRetries > this.MAX_GAP_RETRIES) {
            console.warn('[GAP] too many gaps, stopping playback');
            return;
        }
        console.warn(`[GAP] skipping to ${new Date(this.nextTs + (this.SEGMENT_SEC * 1000)).toLocaleTimeString()}`);
        this.nextTs += (this.SEGMENT_SEC * 1000);
    }

    appendBuffer(buffer) {
        return new Promise(resolve => {
            if (!this.sourceBuffer || this.mediaSource.readyState !== 'open') return resolve();
            const onUpdateEnd = () => {
                this.sourceBuffer.removeEventListener('updateend', onUpdateEnd);
                resolve();
            };
            this.sourceBuffer.addEventListener('updateend', onUpdateEnd);
            try {
                this.sourceBuffer.appendBuffer(buffer);
            } catch (e) {
                console.error("[MSE] Append Error:", e);
                this.sourceBuffer.removeEventListener('updateend', onUpdateEnd);
                resolve();
            }
        });
    }

    getBufferedEnd() {
        if (!this.video || !this.video.buffered || this.video.buffered.length === 0) return 0;
        return this.video.buffered.end(this.video.buffered.length - 1);
    }

    async seekTo(epochMs) {
        if (!this.mediaSource || !this.sourceBuffer) return;

        console.log('[MSE] SEEK TO', new Date(epochMs).toISOString());
        this.isSeeking = true;
        this.seekTargetTs = epochMs;
        this.gapRetries = 0;

        if (this.abortController) {
            this.abortController.abort();
            this.abortController = null;
        }
        this.isFetching = false;

        this.video.pause();

        if (this.sourceBuffer.updating) {
            await new Promise(r => this.sourceBuffer.addEventListener('updateend', r, { once: true }));
        }

        try {
            const ranges = this.video.buffered;
            if (ranges.length > 0) {
                const start = ranges.start(0);
                const end = ranges.end(ranges.length - 1);
                console.log(`[MSE] Clearing Buffer: ${start.toFixed(2)} - ${end.toFixed(2)}`);
                this.sourceBuffer.remove(start, end);
                await new Promise(r => this.sourceBuffer.addEventListener('updateend', r, { once: true }));
            }
        } catch (e) {
            console.warn("[MSE] Buffer flush warning:", e);
        }

        this.video.currentTime = 0;
        this.nextTs = epochMs;
        this.started = false;
        this.isSeeking = false;
    }

    getCurrentEpochMs() {
        if (!this.started || !this.nextTs) return this.nextTs || 0;
        const bufferedDuration = this.getBufferedEnd();
        const playheadPos = this.video.currentTime;
        const timeBehindEnd = Math.max(0, bufferedDuration - playheadPos);
        return this.nextTs - (timeBehindEnd * 1000);
    }

    startDebugLogger() {
        if (this.debugInterval) clearInterval(this.debugInterval);
        this.debugInterval = setInterval(() => {
            if (!this.video || !this.video.buffered || this.video.buffered.length === 0) return;
            const current = this.video.currentTime;
            const end = this.getBufferedEnd();
            console.log(`[MSE] current: ${current.toFixed(1)} bufferedEnd: ${end.toFixed(1)} ahead: ${(end - current).toFixed(1)}`);
        }, 2000);
    }
}
