import React, { useEffect, useState, useRef, useLayoutEffect } from "react";
import { useSearchParams, useNavigate } from "react-router-dom";
import API from "../api";
import PlaybackController, { PlayerStates } from "../services/PlaybackController";
import { ArrowLeft, Play, Pause, ChevronLeft, ChevronRight, ZoomIn, ZoomOut, Calendar, Monitor, Maximize } from "../components/Icons";

// --- THEME ---
const THEME = {
    bg: "#121212",
    panel: "#1e1e1e",
    border: "#333",
    accent: "#007acc",
    text: "#e0e0e0",
    textDim: "#888",
    timelineTrack: "#252525",
    segment: "#4caf50",
    segmentBorder: "#2e7d32",
    playhead: "#ff1744"
};

export default function PlaybackModern() {
    const [searchParams] = useSearchParams();
    const navigate = useNavigate();
    const camId = searchParams.get("camId");

    // --- STATE ---
    const [selectedDate, setSelectedDate] = useState(new Date().toISOString().split('T')[0]);
    const [playerState, setPlayerState] = useState(PlayerStates.IDLE);

    // Timezone Support
    const [serverTimezone, setServerTimezone] = useState(null);

    // Timeline
    const [currentTimeMs, setCurrentTimeMs] = useState(Date.now());
    const [viewStartMs, setViewStartMs] = useState(new Date().setHours(0, 0, 0, 0));
    const [zoomLevel, setZoomLevel] = useState(1);
    const [zoomPxPerSec, setZoomPxPerSec] = useState(0.01);
    const [containerWidth, setContainerWidth] = useState(1000);
    const [segments, setSegments] = useState([]);
    const [isLoadingData, setIsLoadingData] = useState(false);

    const videoRef = useRef(null);
    const playerRef = useRef(null);
    const timelineRef = useRef(null);
    const dragRef = useRef({ isDragging: false, startX: 0, startViewTime: 0 });

    // --- 0. LOAD TIMEZONE ---
    useEffect(() => {
        API.get('/system/time').then(res => {
            if (res.data?.raw?.['Time zone']) {
                const tz = res.data.raw['Time zone'].split(' ')[0];
                setServerTimezone(tz);
                console.log("[Playback] Using Server Timezone:", tz);
            }
        }).catch(err => console.warn("Failed to get server timezone", err));
    }, []);

    useEffect(() => {
        if (selectedDate) {
            const startOfDay = new Date(selectedDate).setHours(0, 0, 0, 0);
            setViewStartMs(startOfDay);
        }
    }, [selectedDate, serverTimezone]);

    // --- 1. INITIALIZE CONTROLLER (Uses FSM Internally) ---
    useEffect(() => {
        if (videoRef.current && camId) {
            console.log("[Playback] Init Controller:", camId);
            playerRef.current = new PlaybackController(videoRef.current, camId, (s, payload) => {
                setPlayerState(s);
            });
            return () => playerRef.current?.destroy();
        }
    }, [camId]);

    // --- 2. LAYOUT & RESIZE ---
    useLayoutEffect(() => {
        const updateWidth = () => {
            if (timelineRef.current) setContainerWidth(timelineRef.current.clientWidth);
        };
        window.addEventListener('resize', updateWidth);
        updateWidth();
        return () => window.removeEventListener('resize', updateWidth);
    }, []);

    // --- 3. LOAD DATA ---
    useEffect(() => {
        if (!camId || !selectedDate) return;

        const fetchData = async () => {
            setIsLoadingData(true);
            try {
                const res = await API.get(`/api/playback/timeline-day/${camId}/${selectedDate}`);
                const data = res.data;
                const segs = (data.segments || []).map(s => ({
                    start: Number(s.start_ts || s.startTs),
                    end: Number(s.end_ts || s.endTs),
                    file: s.file
                })).sort((a, b) => a.start - b.start);

                console.log(`[Playback] Loaded ${segs.length} segments.`);
                setSegments(segs);
                if (playerRef.current) playerRef.current.loadSegments(segs);

                // Auto-fit Day
                if (containerWidth > 0) {
                    const s = new Date(selectedDate).setHours(0, 0, 0, 0);
                    setViewStartMs(s);
                    setZoomPxPerSec(containerWidth / 86400);
                }

            } catch (e) {
                console.error("Timeline Load Error:", e);
            } finally {
                setIsLoadingData(false);
            }
        };
        fetchData();
    }, [camId, selectedDate]);

    // --- 4. PLAYER SYNC ---
    useEffect(() => {
        const i = setInterval(() => {
            if (playerState === PlayerStates.PLAYING && playerRef.current) {
                const now = playerRef.current.getCurrentTime();
                if (Math.abs(now - currentTimeMs) > 200) setCurrentTimeMs(now);
            }
        }, 100);
        return () => clearInterval(i);
    }, [playerState, currentTimeMs]);

    // --- 5. HELPERS ---
    const fitDay = () => {
        const s = new Date(selectedDate).setHours(0, 0, 0, 0);
        setViewStartMs(s);
        setZoomPxPerSec(containerWidth / 86400);
        setZoomLevel(0);
    };

    const zoomIn = () => {
        const newZoom = Math.min(200, zoomPxPerSec * 2);
        const centerTime = viewStartMs + ((containerWidth / 2) / zoomPxPerSec) * 1000;
        setZoomPxPerSec(newZoom);
        setViewStartMs(centerTime - ((containerWidth / 2) / newZoom) * 1000);
    };

    const zoomOut = () => {
        const newZoom = Math.max(containerWidth / 86400, zoomPxPerSec / 2);
        const centerTime = viewStartMs + ((containerWidth / 2) / zoomPxPerSec) * 1000;
        setZoomPxPerSec(newZoom);
        setViewStartMs(centerTime - ((containerWidth / 2) / newZoom) * 1000);
    };

    const performSeek = (ts) => {
        setCurrentTimeMs(ts);
        playerRef.current?.seekTo(ts);
    };

    // --- 6. MOUSE EVENTS ---
    const handleMouseDown = (e) => {
        dragRef.current = { isDragging: true, startX: e.clientX, startView: viewStartMs };
        document.body.style.cursor = 'grabbing';
    };
    const handleMouseMove = (e) => {
        if (!dragRef.current.isDragging) return;
        const dx = dragRef.current.startX - e.clientX;
        const dt = (dx / zoomPxPerSec) * 1000;
        setViewStartMs(dragRef.current.startView + dt);
    };
    const handleMouseUp = (e) => {
        if (dragRef.current.isDragging) {
            dragRef.current.isDragging = false;
            document.body.style.cursor = 'default';
            if (Math.abs(e.clientX - dragRef.current.startX) < 5) {
                const rect = timelineRef.current.getBoundingClientRect();
                const x = e.clientX - rect.left;
                const ts = viewStartMs + (x / zoomPxPerSec) * 1000;
                performSeek(ts);
            }
        }
    };
    const handleWheel = (e) => {
        if (e.target.closest('.timeline-area')) {
            e.preventDefault();
            const rect = timelineRef.current.getBoundingClientRect();
            const mouseX = e.clientX - rect.left;
            const mouseTime = viewStartMs + (mouseX / zoomPxPerSec) * 1000;

            const factor = e.deltaY > 0 ? 0.9 : 1.1;
            const newZoom = Math.max(containerWidth / 86400, Math.min(200, zoomPxPerSec * factor));

            setZoomPxPerSec(newZoom);
            setViewStartMs(mouseTime - (mouseX / newZoom) * 1000);
        }
    };

    // --- RENDER TIMELINE CANVAS ---
    const timeToPx = (t) => (t - viewStartMs) / 1000 * zoomPxPerSec;

    const renderTicks = () => {
        const ticks = [];
        const pxPerMin = zoomPxPerSec * 60;
        let stepMin = 60 * 24;
        if (pxPerMin > 1000) stepMin = 1;
        else if (pxPerMin > 200) stepMin = 5;
        else if (pxPerMin > 50) stepMin = 15;
        else if (pxPerMin > 10) stepMin = 60;
        else if (pxPerMin > 2) stepMin = 240;

        const stepMs = stepMin * 60 * 1000;
        if (stepMs <= 0) return [];

        const start = Math.floor(viewStartMs / stepMs) * stepMs;
        const end = viewStartMs + (containerWidth / zoomPxPerSec * 1000);

        for (let t = start; t < end; t += stepMs) {
            const x = timeToPx(t);
            const date = new Date(t);
            const isHour = date.getMinutes() === 0;
            ticks.push(
                <div key={t} style={{ position: 'absolute', left: x, top: 0, bottom: 0, borderLeft: `1px solid ${isHour ? '#666' : '#333'}`, pointerEvents: 'none' }}>
                    <div style={{ position: 'absolute', bottom: 4, left: 4, fontSize: 10, color: isHour ? '#eee' : '#777', fontFamily: 'monospace' }}>
                        {date.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })}
                    </div>
                </div>
            );
        }
        return ticks;
    };

    const renderSegments = () => {
        const visEnd = viewStartMs + (containerWidth / zoomPxPerSec * 1000);
        return segments.map((s, i) => {
            if (s.end < viewStartMs || s.start > visEnd) return null;
            const x = timeToPx(s.start);
            const w = Math.max(2, (s.end - s.start) / 1000 * zoomPxPerSec);
            return (
                <div key={i} title={new Date(s.start).toLocaleTimeString()}
                    style={{
                        position: 'absolute', left: x, width: w,
                        top: 20, height: 40,
                        background: THEME.segment,
                        border: `1px solid ${THEME.segmentBorder}`,
                        borderRadius: 2,
                        opacity: 0.9
                    }}
                />
            );
        });
    };

    return (
        <div style={{ display: 'flex', flexDirection: 'column', height: '100vh', background: THEME.bg, color: THEME.text, fontFamily: 'Segoe UI, sans-serif' }}>
            {/* HEADER */}
            <header style={{ height: 50, background: THEME.panel, borderBottom: `1px solid ${THEME.border}`, display: 'flex', alignItems: 'center', padding: '0 16px', gap: 16 }}>
                <button onClick={() => navigate('/')} style={{ background: 'none', border: `1px solid ${THEME.border}`, color: THEME.text, padding: '6px 12px', borderRadius: 4, cursor: 'pointer', fontSize: 13 }}>
                    ← LIVE
                </button>
                <div style={{ fontWeight: 600, fontSize: 16 }}>PLAYBACK</div>
                <div style={{ width: 1, height: 20, background: THEME.border }} />

                {/* TOOLBAR */}
                <button onClick={() => playerRef.current?.seekTo(currentTimeMs - 10000)} style={{ ...btnStyle }}>⏪ -10s</button>
                <button onClick={() => playerState === PlayerStates.PLAYING ? playerRef.current?.pause() : playerRef.current?.play()}
                    style={{ ...btnStyle, background: THEME.accent, border: 'none', color: '#fff', fontWeight: 'bold' }}>
                    {playerState === PlayerStates.PLAYING ? "PAUSE" : "PLAY"}
                </button>
                <button onClick={() => playerRef.current?.seekTo(currentTimeMs + 10000)} style={{ ...btnStyle }}>⏩ +10s</button>

                <div style={{ flex: 1 }} />

                {/* INFO */}
                {serverTimezone && <div style={{ fontSize: 11, color: '#666', marginRight: 10 }}>TZ: {serverTimezone}</div>}

                {/* DATE PICKER */}
                <div style={{ display: 'flex', alignItems: 'center', gap: 8, background: '#000', padding: '4px 8px', borderRadius: 4, border: `1px solid ${THEME.border}` }}>
                    <span style={{ fontSize: 12, color: THEME.textDim }}>DATE</span>
                    <input type="date" value={selectedDate} onChange={e => setSelectedDate(e.target.value)}
                        style={{ background: 'transparent', border: 'none', color: '#fff', fontFamily: 'inherit', outline: 'none' }} />
                </div>
            </header>

            {/* VIDEO AREA */}
            <div style={{ flex: 1, background: '#000', position: 'relative', display: 'flex', alignItems: 'center', justifyContent: 'center' }}>
                <video ref={videoRef} style={{ width: '100%', height: '100%' }} onClick={() => playerRef.current?.play()} />

                {isLoadingData && <div style={overlayStyle}>Loading Timeline...</div>}
                {playerState === PlayerStates.BUFFERING && <div style={overlayStyle}>Buffering...</div>}
                {!isLoadingData && segments.length === 0 && <div style={{ ...overlayStyle, top: '40%' }}>No Recordings Found</div>}

                <div style={{ position: 'absolute', top: 16, right: 16, background: 'rgba(0,0,0,0.6)', padding: '6px 12px', borderRadius: 4, fontFamily: 'monospace', fontSize: 14, color: '#0f0' }}>
                    {new Date(currentTimeMs).toLocaleTimeString()}
                </div>
            </div>

            {/* TIMELINE AREA */}
            <div className="timeline-area"
                ref={timelineRef}
                style={{ height: 120, background: THEME.timelineTrack, borderTop: `1px solid ${THEME.border}`, position: 'relative', overflow: 'hidden', cursor: dragRef.current.isDragging ? 'grabbing' : 'grab' }}
                onMouseUp={handleMouseUp} onMouseLeave={handleMouseUp} onMouseMove={handleMouseMove} onMouseDown={handleMouseDown} onWheel={handleWheel}
            >
                <div style={{ position: 'absolute', top: 4, right: 10, zIndex: 20, display: 'flex', gap: 4 }}>
                    <button onClick={zoomOut} style={btnSmall}>-</button>
                    <button onClick={fitDay} style={btnSmall}>FIT</button>
                    <button onClick={zoomIn} style={btnSmall}>+</button>
                </div>

                {renderTicks()}
                <div style={{ position: 'absolute', top: 0, left: 0, right: 0, bottom: 0, pointerEvents: 'none' }}>
                    {renderSegments()}
                </div>

                <div style={{ position: 'absolute', left: timeToPx(currentTimeMs), top: 0, bottom: 0, width: 2, background: THEME.playhead, zIndex: 10, pointerEvents: 'none' }}>
                    <div style={{ position: 'absolute', top: 0, left: -6, borderLeft: '7px solid transparent', borderRight: '7px solid transparent', borderTop: `8px solid ${THEME.playhead}` }} />
                </div>
            </div>
        </div>
    );
}

const btnStyle = { background: '#222', border: '1px solid #444', color: '#ddd', padding: '6px 12px', borderRadius: 4, cursor: 'pointer', fontSize: 12 };
const btnSmall = { background: '#333', border: '1px solid #555', color: '#fff', width: 24, height: 24, padding: 0, borderRadius: 2, cursor: 'pointer', fontSize: 10 };
const overlayStyle = { position: 'absolute', background: 'rgba(0,0,0,0.7)', color: '#fff', padding: '10px 20px', borderRadius: 8, fontSize: 14, fontWeight: 'bold' };
