import React, { useEffect, useRef, useState } from 'react';
import { MediaStreamManager } from './MediaStreamManager';

function Go2RTCPlayer({ camId, streamType = 'hd', style, onClick, onDoubleClick, isHidden }) {
    const videoRef = useRef(null);
    const [status, setStatus] = useState("init");

    // Fallback logic for stream suffix
    const suffix =
        streamType === 'low' ? 'sub' :
            streamType === 'hd' ? 'hd' :
                'sub';

    useEffect(() => {
        if (isHidden) return;

        let mounted = true;
        const manager = MediaStreamManager.getInstance();

        const attachStream = async () => {
            setStatus("connecting");
            try {
                // Request persistent stream from singleton
                const stream = await manager.getStream(camId, suffix);

                if (mounted && videoRef.current) {
                    videoRef.current.srcObject = stream;
                    videoRef.current.play().catch(e => console.warn("Autoplay blocked", e));
                    setStatus("playing");
                }
            } catch (e) {
                console.error("[Go2RTCPlayer] Failed to get stream:", e);
                if (mounted) setStatus("error");
            }
        };

        attachStream();

        return () => {
            mounted = false;
            // Detach but don't close the connection
            if (videoRef.current) {
                videoRef.current.srcObject = null;
            }
            manager.releaseStream(camId, suffix);
        };
    }, [camId, suffix, isHidden]);

    return (
        <div className="rtc-player" style={{ ...style, position: "relative", background: "#000", width: "100%", height: "100%", overflow: "hidden" }}
            onClick={onClick} onDoubleClick={onDoubleClick}>
            <div style={{ position: "absolute", top: 0, left: 0, right: 0, bottom: 0 }}>
                <video
                    className="rtc-player-el"
                    ref={videoRef}
                    style={{ width: "100%", height: "100%", objectFit: "fill", display: "block" }}
                    playsInline muted autoPlay
                />
            </div>
            {status === 'connecting' && (
                <div style={{ position: "absolute", top: 0, left: 0, width: "100%", height: "100%", display: "flex", alignItems: "center", justifyContent: "center", color: "rgba(255,255,255,0.4)", fontSize: 10, zIndex: 2 }}>
                    ...
                </div>
            )}
        </div>
    );
}

export default React.memo(Go2RTCPlayer);
