import React, { useEffect, useState, useRef } from "react";
import { useSearchParams } from "react-router-dom";
import API from "../api";
import Hls from 'hls.js';

const styles = `
* { box-sizing: border-box; margin: 0; padding: 0; }
body { background: #cfd2d6; }

.playback-page {
  height: 100vh;
  display: flex;
  flex-direction: column;
  background: #cfd2d6;
  font-family: Arial, sans-serif;
}

.playback-header {
  height: 40px;
  background: #d9d9d9;
  display: flex;
  align-items: center;
  padding: 0 10px;
  gap: 8px;
  border-bottom: 1px solid #999;
}

.video-viewport {
  height: calc(100vh - 140px);
  background: #000;
  display: flex;
  align-items: center;
  justify-content: center;
}

video {
  max-width: 100%;
  max-height: 100%;
  background: #000;
}

.controls {
  height: 60px;
  background: #d9d9d9;
  display: flex;
  align-items: center;
  gap: 6px;
  padding: 0 8px;
  border-bottom: 1px solid #999;
}

.timeline-area {
  height: 80px;
  background: #e6e6e6;
  border-top: 1px solid #999;
  position: relative;
}

.segments-track {
  position: absolute;
  top: 34px;
  height: 12px;
  left: 0;
  right: 0;
}

.segment {
  position: absolute;
  height: 100%;
  background: #2ecc71;
}

button {
  padding: 4px 10px;
  border: 1px solid #555;
  background: #f0f0f0;
  cursor: pointer;
  font-size: 13px;
}

button:hover { background: #fff; }
button:active { background: #ddd; }

.time-display {
  margin-left: auto;
  font-weight: bold;
}
`;

export default function Playback() {
    const [params] = useSearchParams();
    const camId = params.get("camId");

    const [segments, setSegments] = useState([]);
    const [isPlaying, setIsPlaying] = useState(false);
    const [currentTime, setCurrentTime] = useState("00:00:00");

    const videoRef = useRef(null);
    const hlsRef = useRef(null);

    const [timeRange] = useState(() => {
        const now = Date.now();
        const from = now - 24 * 60 * 60 * 1000;
        return { from, to: now };
    });

    // Load timeline
    useEffect(() => {
        if (!camId) return;

        API.get(`/playback/timeline/${camId}?from=${timeRange.from}&to=${timeRange.to}`)
            .then(res => {
                const segs = res.data.segments || [];
                const valid = segs.filter(s => s.start_ts > 0 && s.start_ts < timeRange.to * 2);
                setSegments(valid);
            })
            .catch(e => console.error("Timeline error:", e));
    }, [camId]);

    //Start playback
    const startPlayback = async () => {
        if (segments.length === 0) {
            alert("Nicio înregistrare");
            return;
        }

        try {
            const res = await API.post('/playback/start', {
                camId,
                from: segments[0].start_ts,
                to: segments[segments.length - 1].end_ts || Date.now(),
                speed: 1
            });

            console.log('[Playback] Response:', res.data);

            // Load HLS stream
            const hlsUrl = `http://192.168.120.208:8080${res.data.hlsUrl}`;

            if (Hls.isSupported()) {
                const hls = new Hls();
                hls.loadSource(hlsUrl);
                hls.attachMedia(videoRef.current);
                hls.on(Hls.Events.MANIFEST_PARSED, () => {
                    videoRef.current.play();
                });
                hlsRef.current = hls;
            } else if (videoRef.current.canPlayType('application/vnd.apple.mpegurl')) {
                videoRef.current.src = hlsUrl;
                videoRef.current.addEventListener('loadedmetadata', () => {
                    videoRef.current.play();
                });
            }

            setIsPlaying(true);
        } catch (e) {
            console.error('[Playback] Error:', e);
            alert('Eroare: ' + (e.response?.data?.error || e.message));
        }
    };

    const stopPlayback = async () => {
        try {
            await API.post('/playback/stop', { camId });

            if (hlsRef.current) {
                hlsRef.current.destroy();
                hlsRef.current = null;
            }

            if (videoRef.current) {
                videoRef.current.pause();
                videoRef.current.src = '';
            }

            setIsPlaying(false);
        } catch (e) {
            console.error(e);
        }
    };

    // Update time
    useEffect(() => {
        const interval = setInterval(() => {
            if (videoRef.current) {
                const t = Math.floor(videoRef.current.currentTime);
                const h = String(Math.floor(t / 3600)).padStart(2, '0');
                const m = String(Math.floor((t % 3600) / 60)).padStart(2, '0');
                const s = String(t % 60).padStart(2, '0');
                setCurrentTime(`${h}:${m}:${s}`);
            }
        }, 500);

        return () => clearInterval(interval);
    }, []);

    return (
        <div className="playback-page">
            <style>{styles}</style>

            <div className="playback-header">
                <button onClick={() => window.location.href = '/'}>← Înapoi</button>
                <span style={{ fontWeight: 600 }}>{camId} - PLAYBACK (HLS)</span>
                <span style={{ fontSize: 11, color: '#555' }}>Segmente: {segments.length}</span>
            </div>

            <div className="video-viewport">
                <video ref={videoRef} controls autoPlay muted />
            </div>

            <div className="controls">
                {!isPlaying ? (
                    <button onClick={startPlayback}>▶ REDĂ</button>
                ) : (
                    <button onClick={stopPlayback}>■ OPRIRE</button>
                )}
                <div className="time-display">{currentTime}</div>
            </div>

            <div className="timeline-area">
                <div className="segments-track">
                    {segments.map((seg, i) => (
                        <div
                            key={i}
                            className="segment"
                            style={{
                                left: `${seg.s * 100}%`,
                                width: `${Math.max((seg.e - seg.s) * 100, 0.5)}%`
                            }}
                        />
                    ))}
                </div>
            </div>
        </div>
    );
}
