import React, { useEffect, useState, useRef, useLayoutEffect } from "react";
import { useSearchParams, useNavigate } from "react-router-dom";
import API from "../api";
import PlaybackController, { PlayerStates } from "../services/PlaybackController";
import { ArrowLeft, Play, Pause, ChevronLeft, ChevronRight, ZoomIn, ZoomOut, Calendar, Monitor, Maximize } from "../components/Icons";

// --- AZIGUARD THEME REPLICA ---
const THEME = {
    bg: "#f0f2f5", // Light grey
    header: "#ffffff",
    headerBorder: "#e1e4e8",
    accent: "#0277bd", // Deep Blue
    accentHover: "#01579b",
    text: "#333333",
    textLight: "#666666",
    timelineTrack: "#ffffff",
    timelineRuler: "#e0e0e0",
    segment: "#d32f2f", // Red
    playhead: "#d32f2f", // Red playback line per user request
    panelBorder: "#dce1e6"
};

export default function PlaybackModern() {
    const [searchParams] = useSearchParams();
    const navigate = useNavigate();
    const camId = searchParams.get("camId");

    // --- STATE ---
    const [selectedDate, setSelectedDate] = useState(new Date().toISOString().split('T')[0]);
    const [playerState, setPlayerState] = useState(PlayerStates.IDLE);
    const [serverTimezone, setServerTimezone] = useState(null);

    // --- TIMELINE STATE ---
    // 'currentTimeMs': The exact moment the video is playing
    const [currentTimeMs, setCurrentTimeMs] = useState(Date.now());

    // 'viewportStartMs': The timestamp at the LEFT edge of the visible timeline window
    // This replaces 'viewStartMs' which was sometimes centered.
    // Panning changes viewportStartMs.
    const [viewportStartMs, setViewportStartMs] = useState(new Date().setHours(0, 0, 0, 0));

    // Zoom: pixels per second
    const [zoomPxPerSec, setZoomPxPerSec] = useState(0.005); // Initial zoom fitting ~5h roughly

    const [containerWidth, setContainerWidth] = useState(1000);
    const [segments, setSegments] = useState([]);
    const [isLoadingData, setIsLoadingData] = useState(false);

    const videoRef = useRef(null);
    const playerRef = useRef(null);
    const timelineRef = useRef(null);
    const dragRef = useRef({ isDragging: false, startX: 0, startViewTime: 0 });

    // --- DATA LOADING & SETUP ---
    useEffect(() => {
        API.get('/system/time').then(res => {
            if (res.data?.raw?.['Time zone']) setServerTimezone(res.data.raw['Time zone'].split(' ')[0]);
        }).catch(() => { });
    }, []);

    useEffect(() => {
        if (selectedDate) setViewportStartMs(new Date(selectedDate).setHours(0, 0, 0, 0));
    }, [selectedDate]);

    // --- CONTROLLER INIT ---
    useEffect(() => {
        if (videoRef.current && camId) {
            playerRef.current = new PlaybackController(videoRef.current, camId, (s) => setPlayerState(s));
            return () => playerRef.current?.destroy();
        }
    }, [camId]);

    // --- RESIZE HANDLER ---
    useLayoutEffect(() => {
        const updateWidth = () => { if (timelineRef.current) setContainerWidth(timelineRef.current.clientWidth); };
        window.addEventListener('resize', updateWidth);
        updateWidth();
        return () => window.removeEventListener('resize', updateWidth);
    }, []);

    // --- LOAD SEGMENTS ---
    useEffect(() => {
        if (!camId || !selectedDate) return;
        const fetchData = async () => {
            setIsLoadingData(true);
            try {
                const res = await API.get(`/api/playback/timeline-day/${camId}/${selectedDate}`);
                const data = res.data;
                const segs = (data.segments || []).map(s => ({
                    start: Number(s.start_ts || s.startTs),
                    end: Number(s.end_ts || s.endTs),
                    file: s.file
                })).sort((a, b) => a.start - b.start);
                setSegments(segs);
                if (playerRef.current) playerRef.current.loadSegments(segs);

                // Auto Fit Day (0-24h)
                if (containerWidth > 0) {
                    setViewportStartMs(new Date(selectedDate).setHours(0, 0, 0, 0));
                    // Fit 24h into width: width / 86400
                    setZoomPxPerSec(containerWidth / 86400);
                }
            } catch (e) { console.error(e); } finally { setIsLoadingData(false); }
        };
        fetchData();
    }, [camId, selectedDate]);

    // --- SYNC LOOP ---
    useEffect(() => {
        const i = setInterval(() => {
            if (playerState === PlayerStates.PLAYING && playerRef.current) {
                const now = playerRef.current.getCurrentTime();
                if (Math.abs(now - currentTimeMs) > 200) setCurrentTimeMs(now);

                // Optional: Auto-scroll if playhead goes out of view? 
                // Currently user requested standard timeline behavior which implies usually manual or page flip
            }
        }, 100);
        return () => clearInterval(i);
    }, [playerState, currentTimeMs]);

    // --- ACTIONS ---
    const fitDay = () => {
        setViewportStartMs(new Date(selectedDate).setHours(0, 0, 0, 0));
        setZoomPxPerSec(containerWidth / 86400);
    };

    const performSeek = (ts) => {
        setCurrentTimeMs(ts);
        playerRef.current?.seekTo(ts);
    };

    // --- MOUSE EVENTS (UPDATED FOR STATIC TIMELINE / MOVING PLAYHEAD) ---
    const handleMouseDown = (e) => {
        // Panning Logic
        dragRef.current = { isDragging: true, startX: e.clientX, startViewTime: viewportStartMs };
        document.body.style.cursor = 'grabbing';
    };

    const handleMouseMove = (e) => {
        if (!dragRef.current.isDragging) return;
        const dx = e.clientX - dragRef.current.startX;
        // Move viewport opposite to drag direction for "grab and pull" feel
        const dt = -(dx / zoomPxPerSec) * 1000;
        setViewportStartMs(dragRef.current.startViewTime + dt);
    };

    const handleMouseUp = (e) => {
        if (dragRef.current.isDragging) {
            dragRef.current.isDragging = false;
            document.body.style.cursor = 'default';
            // Click to Seek if it was a short click (not a drag)
            if (Math.abs(e.clientX - dragRef.current.startX) < 5) {
                const rect = timelineRef.current.getBoundingClientRect();
                const clickX = e.clientX - rect.left;
                const ts = viewportStartMs + (clickX / zoomPxPerSec) * 1000;
                performSeek(ts);
            }
        }
    };

    const handleWheel = (e) => {
        if (e.target.closest('.timeline-area')) {
            e.preventDefault();
            const rect = timelineRef.current.getBoundingClientRect();
            const mouseX = e.clientX - rect.left;

            // Calculate time exactly under the mouse pointer
            const timeUnderMouse = viewportStartMs + (mouseX / zoomPxPerSec) * 1000;

            const factor = e.deltaY > 0 ? 0.9 : 1.1;
            const newZoom = Math.max(containerWidth / 86400, Math.min(2.0, zoomPxPerSec * factor));

            setZoomPxPerSec(newZoom);

            // Adjust viewportStartMs so that 'timeUnderMouse' remains at 'mouseX'
            // mouseX = (timeUnderMouse - newViewportStart) / 1000 * newZoom
            // newViewportStart = timeUnderMouse - (mouseX / newZoom) * 1000
            setViewportStartMs(timeUnderMouse - (mouseX / newZoom) * 1000);
        }
    };

    // --- RENDER HELPERS ---
    // Converts absolute timestamp to pixel X coordinate relative to container left
    const timeToPx = (t) => (t - viewportStartMs) / 1000 * zoomPxPerSec;

    const renderTicks = () => {
        const ticks = [];
        const pxPerMin = zoomPxPerSec * 60;
        let stepMin = 60 * 24;
        if (pxPerMin > 1000) stepMin = 1; else if (pxPerMin > 200) stepMin = 5; else if (pxPerMin > 50) stepMin = 15; else if (pxPerMin > 10) stepMin = 60; else stepMin = 240;

        const stepMs = stepMin * 60 * 1000;
        if (stepMs <= 0) return [];

        const start = Math.floor(viewportStartMs / stepMs) * stepMs;
        const end = viewportStartMs + (containerWidth / zoomPxPerSec * 1000) + stepMs; // +stepMs to catch edge

        for (let t = start; t < end; t += stepMs) {
            const x = timeToPx(t);
            const date = new Date(t);
            const isHour = date.getMinutes() === 0;
            if (x < -50 || x > containerWidth + 50) continue; // Skip out of bounds

            ticks.push(
                <div key={t} style={{ position: 'absolute', left: x, height: '100%', borderLeft: `1px solid ${isHour ? '#999' : '#ddd'}`, pointerEvents: 'none' }}>
                    {isHour && <div style={{ position: 'absolute', top: 4, left: 4, fontSize: 11, fontWeight: 600, color: '#333' }}>
                        {date.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })}
                    </div>}
                </div>
            );
        }
        return ticks;
    };

    return (
        <div style={{ display: 'flex', flexDirection: 'column', height: '100vh', background: THEME.bg, color: THEME.text, fontFamily: '-apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, Helvetica, Arial, sans-serif' }}>

            {/* 1. TOP BAR */}
            <header style={{ height: 48, background: '#fff', borderBottom: `1px solid ${THEME.panelBorder}`, display: 'flex', alignItems: 'center', padding: '0 16px' }}>
                <div style={{ fontWeight: 800, fontSize: 18, color: THEME.accent, marginRight: 24 }}>AZIGUARD</div>
                <div style={{ display: 'flex', gap: 8 }}>
                    {['Live', 'Playback', 'Settings'].map(tab => (
                        <div key={tab} style={{
                            padding: '6px 12px', borderRadius: 4, cursor: 'pointer', fontSize: 13, fontWeight: 500,
                            background: tab === 'Playback' ? '#e3f2fd' : 'transparent', color: tab === 'Playback' ? THEME.accent : '#666'
                        }} onClick={() => tab === 'Live' && navigate('/')}>{tab}</div>
                    ))}
                </div>
                <div style={{ flex: 1 }} />
                <div style={{ fontSize: 13, color: '#555', display: 'flex', alignItems: 'center', gap: 10 }}>
                    <b>{serverTimezone}</b>
                    <input type="date" value={selectedDate} onChange={e => setSelectedDate(e.target.value)} style={{ border: '1px solid #ccc', borderRadius: 4, padding: '4px' }} />
                </div>
            </header>

            {/* 2. MAIN CONTENT (Video 70% height implied, Controls bottom) */}
            <div style={{ flex: 1, background: '#e0e0e0', position: 'relative', display: 'flex', alignItems: 'center', justifyContent: 'center' }}>
                {/* VIDEO CONTAINER */}
                <div style={{ width: '100%', height: '100%', maxHeight: 'calc(100vh - 200px)', display: 'flex', justifyContent: 'center', background: '#000' }}>
                    <video ref={videoRef} style={{ width: '100%', height: '100%' }} onClick={() => playerRef.current?.play()} />
                </div>

                {isLoadingData && <div style={overlayStyle}>Loading...</div>}
                {!isLoadingData && segments.length === 0 && <div style={{ ...overlayStyle, top: '40%' }}>No Recordings</div>}
            </div>

            {/* 3. FOOTER (Summary + Timeline) */}
            <div style={{ background: '#fff', borderTop: `1px solid ${THEME.panelBorder}`, display: 'flex', flexDirection: 'column', overflow: 'hidden' }}>

                {/* A. SUMMARY BAR (0-24h View) - Replaces requested "full day bar" */}
                {/* B. MAIN TIMELINE */}

                {/* Controls Overlay on top of timeline or separate row? Using separate row for clarity */}
                <div style={{ height: 36, display: 'flex', alignItems: 'center', padding: '0 10px', borderBottom: '1px solid #eee', background: '#fcfcfc' }}>
                    <button onClick={() => playerRef.current?.seekTo(currentTimeMs - 5000)} style={ctrlBtn}>-5s</button>
                    <button onClick={() => playerState === PlayerStates.PLAYING ? playerRef.current?.pause() : playerRef.current?.play()}
                        style={{ ...ctrlBtn, color: THEME.accent, fontWeight: 'bold', minWidth: 60 }}>
                        {playerState === PlayerStates.PLAYING ? "PAUSE" : "PLAY"}
                    </button>
                    <button onClick={() => playerRef.current?.seekTo(currentTimeMs + 5000)} style={ctrlBtn}>+5s</button>
                    <div style={{ width: 20 }} />
                    <button onClick={fitDay} style={ctrlBtn}>24H View</button>
                    <div style={{ flex: 1, textAlign: 'center', fontWeight: 'bold', color: '#444' }}>
                        {new Date(currentTimeMs).toLocaleTimeString()}
                    </div>
                </div>

                <div className="timeline-area"
                    ref={timelineRef}
                    style={{ height: 100, background: THEME.timelineTrack, position: 'relative', overflow: 'hidden', cursor: dragRef.current.isDragging ? 'grabbing' : 'grab' }}
                    onMouseUp={handleMouseUp} onMouseLeave={handleMouseUp} onMouseMove={handleMouseMove} onMouseDown={handleMouseDown} onWheel={handleWheel}
                >
                    {/* Ticks */}
                    {renderTicks()}

                    {/* Segments */}
                    {segments.map((s, i) => {
                        const x = timeToPx(s.start);
                        const w = Math.max(1, (s.end - s.start) / 1000 * zoomPxPerSec);
                        if (x + w < 0 || x > containerWidth) return null;
                        return (
                            <div key={i} style={{
                                position: 'absolute', left: x, width: w, top: 45, height: 25,
                                background: THEME.segment, borderRadius: 1
                            }} />
                        );
                    })}

                    {/* Playhead (Moving Red Line) */}
                    {/* Calculated based on currentTimeMs */}
                    <div style={{
                        position: 'absolute',
                        left: timeToPx(currentTimeMs),
                        top: 0, bottom: 0, width: 2,
                        background: THEME.playhead,
                        zIndex: 10, pointerEvents: 'none'
                    }}>
                        <div style={{
                            position: 'absolute', top: 0, left: -5,
                            width: 12, height: 12, borderRadius: '50%',
                            background: THEME.playhead
                        }} />
                    </div>
                </div>
            </div>
        </div>
    );
}

const ctrlBtn = { border: '1px solid #ddd', background: '#fff', borderRadius: 3, padding: '4px 8px', margin: '0 4px', cursor: 'pointer', fontSize: 12 };
const overlayStyle = { position: 'absolute', background: 'rgba(255,255,255,0.85)', padding: '10px 20px', borderRadius: 6, fontWeight: 'bold' };
