import React, { useEffect, useState, useRef, useCallback } from "react";
import { useSearchParams } from "react-router-dom";
import API from "../api";
import Hls from 'hls.js';

const styles = `
* { box-sizing: border-box; margin: 0; padding: 0; }
body { background: #cfd2d6; }
.playback-page { height: 100vh; display: flex; flex-direction: column; background: #cfd2d6; font-family: Arial, sans-serif; }
.playback-header { height: 40px; background: #d9d9d9; display: flex; align-items: center; padding: 0 10px; gap: 8px; border-bottom: 1px solid #999; }
.video-viewport { height: calc(100vh - 140px); background: #000; display: flex; align-items: center; justify-content: center; position: relative; }
video { width: 100%; height: 100%; object-fit: contain; background: #000; }
.controls { height: 60px; background: #d9d9d9; display: flex; align-items: center; gap: 6px; padding: 0 8px; border-bottom: 1px solid #999; }
.timeline-area { height: 80px; background: #e6e6e6; border-top: 1px solid #999; position: relative; overflow: hidden; cursor: crosshair; user-select: none; z-index: 10; }
.time-markers { position: absolute; top: 0; left: 0; width: 100%; height: 20px; display: flex; pointer-events: none; border-bottom: 1px solid #ccc; }
.time-marker { flex: 1; border-left: 1px solid #999; font-size: 10px; color: #555; padding-left: 2px; height: 100%; }
.segments-track { position: absolute; top: 25px; height: 30px; left: 0; right: 0; background: rgba(0,0,0,0.05); pointer-events: none; }
.segment { position: absolute; height: 100%; background: #2ecc71; opacity: 0.8; }
.cursor { position: absolute; top: 0; height: 100%; width: 2px; background: red; z-index: 10; pointer-events: none; }
button { padding: 4px 10px; border: 1px solid #555; background: #f0f0f0; cursor: pointer; font-size: 13px; }
button:hover { background: #fff; }
button:disabled { color: #aaa; cursor: not-allowed; }
.info-text { font-size: 11px; color: #555; margin-left: 10px; }
`;

export default function Playback() {
    const [params] = useSearchParams();
    const camId = params.get("camId");

    // STATE
    const [timelineSegments, setTimelineSegments] = useState([]);
    const [serverDayStart, setServerDayStart] = useState(0);

    // View Date (Local string, sent to server)
    const [selectedDate, setSelectedDate] = useState(() => {
        const d = new Date();
        const offset = d.getTimezoneOffset() * 60000;
        const local = new Date(d.getTime() - offset);
        return local.toISOString().split('T')[0];
    });

    const [isPlaying, setIsPlaying] = useState(false);
    const [isLoading, setIsLoading] = useState(false);

    const videoRef = useRef(null);
    const hlsRef = useRef(null);
    const cursorRef = useRef(null);
    const timelineRef = useRef(null);

    // FETCH TIMELINE
    useEffect(() => {
        if (!camId || !selectedDate) return;

        console.log(`[Timeline] Fetching FULL DAY: ${selectedDate}`);

        API.get(`/playback/timeline-day/${camId}/${selectedDate}`)
            .then(res => {
                const { segments: raw, dayStart } = res.data;

                // 1. USE SERVER DAY START (Miezul noptii pe server)
                // Backend-ul trebuie sa trimita dayStart = new Date(y,m,d,0,0,0,0).getTime()
                setServerDayStart(dayStart);

                const DAY_MS = 86400000;
                const dayEnd = dayStart + DAY_MS;

                // 2. CONVERSIE SI FILTRARE (Fixul Tau)
                const norm = raw.map(e => {
                    const start = Math.max(Number(e.start_ts), dayStart);
                    const end = Math.min(Number(e.end_ts), dayEnd);

                    if (end <= start) return null; // Invalid or outside day

                    // Relative to DayStart (0..1)
                    const left = (start - dayStart) / DAY_MS;
                    const width = (end - start) / DAY_MS;

                    return { left, width, ts: Number(e.start_ts) };
                }).filter(Boolean);

                setTimelineSegments(norm);
                console.log(`[Timeline] Loaded ${norm.length} valid segments. ServerDayStart: ${dayStart}`);
            })
            .catch(e => console.error(e));
    }, [camId, selectedDate]);

    // HLS Helper
    const waitForHls = async (url, retries = 50) => {
        for (let i = 0; i < retries; i++) {
            try {
                const res = await fetch(url, { method: 'HEAD' });
                if (res.ok) return true;
            } catch { /* ignore */ }
            await new Promise(r => setTimeout(r, 200));
        }
        return false;
    };

    const play = useCallback(async (startTime) => {
        if (!startTime) return alert("Selectează un moment pe timeline");
        setIsLoading(true);
        try {
            try {
                API.post('/playback/stop', { camId });
                if (hlsRef.current) { hlsRef.current.destroy(); hlsRef.current = null; }
            } catch (e) { }

            console.log(`[Playback] REQUEST TS: ${startTime} (${new Date(startTime).toLocaleTimeString()})`);

            const res = await API.post('/playback/start', {
                camId,
                from: startTime,
                to: Date.now(),
                speed: 1
            });

            const hlsUrl = `http://192.168.120.208:8080${res.data.hlsUrl}`;
            // Anti-Cache
            const videoUrl = `${hlsUrl}?t=${Date.now()}`;

            if (!await waitForHls(hlsUrl)) throw new Error("HLS Timeout");

            if (Hls.isSupported()) {
                const hls = new Hls();
                hls.loadSource(videoUrl);
                hls.attachMedia(videoRef.current);
                hls.on(Hls.Events.MANIFEST_PARSED, () => videoRef.current.play().catch(console.error));
                hlsRef.current = hls;
            } else if (videoRef.current.canPlayType('application/vnd.apple.mpegurl')) {
                videoRef.current.src = videoUrl;
                videoRef.current.play();
            }
            setIsPlaying(true);
        } catch (e) {
            console.error(e);
        } finally {
            setIsLoading(false);
        }
    }, [camId]);

    const stop = async () => {
        try {
            API.post('/playback/stop', { camId });
            if (hlsRef.current) { hlsRef.current.destroy(); hlsRef.current = null; }
            if (videoRef.current) {
                videoRef.current.pause();
                videoRef.current.removeAttribute('src');
                videoRef.current.load();
            }
            setIsPlaying(false);
        } catch (e) { console.error(e); }
    };

    // TIMELINE CLICK (Server Time Seek)
    const handleSeek = (e) => {
        if (!serverDayStart) return;
        const rect = timelineRef.current.getBoundingClientRect();

        const x = e.clientX - rect.left;
        const width = rect.width;

        // CLICK LOGIC: DayStart + Ratio * 24h
        const ratio = Math.max(0, Math.min(1, x / width));
        const seekTs = Math.floor(serverDayStart + ratio * 86400000);

        if (cursorRef.current) cursorRef.current.style.left = (ratio * 100) + "%";
        console.log(`[Seek] Ratio: ${ratio.toFixed(4)} -> TS: ${seekTs}`);
        play(seekTs);
    };

    const handleMainPlay = () => {
        if (timelineSegments.length > 0) play(timelineSegments[0].ts);
        else alert("Nu există înregistrări.");
    };

    return (
        <div className="playback-page">
            <style>{styles}</style>

            <div className="playback-header">
                <button onClick={() => window.location.href = '/'}>← Înapoi</button>
                <span style={{ fontWeight: 600 }}>{camId}</span>
                <span className="info-text">
                    Intervale: {timelineSegments.length} | Dată: {selectedDate} (Server Aligned)
                </span>
            </div>

            <div className="video-viewport">
                <video ref={videoRef} controls autoPlay muted playsInline />
            </div>

            <div className="controls">
                {!isPlaying ? (
                    <button onClick={handleMainPlay} disabled={isLoading}>
                        {isLoading ? "Încărcare..." : "▶ REDĂ TOATE"}
                    </button>
                ) : (
                    <button onClick={stop}>■ OPRIRE</button>
                )}
            </div>

            <div
                className="timeline-area"
                ref={timelineRef}
                onClick={handleSeek}
                title="Click pentru a reda"
            >
                <div className="time-markers">
                    {Array.from({ length: 24 }, (_, i) => i).map(h => <div key={h} className="time-marker">{h}</div>)}
                </div>

                <div className="segments-track">
                    {timelineSegments.map((s, i) => (
                        <div key={i} className="segment"
                            style={{
                                left: `${s.left * 100}%`,
                                width: `${Math.max(s.width * 100, 0.002)}%` // min 0.002%
                            }}
                        />
                    ))}
                </div>

                <div ref={cursorRef} className="cursor" style={{ left: '0%' }} />
            </div>
        </div>
    );
}
