import Hls from 'hls.js';

export default class PlaybackCoreV2 {
    constructor(videoElement, camId, baseUrl = '/api') {
        this.video = videoElement;
        this.camId = camId;
        this.baseUrl = baseUrl;
        this.hls = null;
        this.currentFragPDT = 0;
        this.currentFragStartPTS = 0;
    }

    setSegments(segments) { }

    start(startEpochMs) {
        // CRITICAL: Single static URL that backend manages as sliding window
        // Backend will serve different content based on current playback position
        // but URL stays the same - this is the LIVE HLS pattern
        const playlistUrl = `${this.baseUrl}/playback/playlist/${this.camId}.m3u8?start=${startEpochMs}&end=${startEpochMs + 60 * 60 * 1000}`;

        if (!this.hls && Hls.isSupported()) {
            this.hls = new Hls({
                debug: false,
                enableWorker: true,

                // CRITICAL: Treat as LIVE stream (even though it's archive)
                liveSyncDurationCount: 3,
                liveMaxLatencyDurationCount: Infinity,
                liveDurationInfinity: true,

                // Manifest reloading
                manifestLoadingTimeOut: 10000,
                manifestLoadingMaxRetry: Infinity,
                manifestLoadingRetryDelay: 1000,

                // Fragment loading
                fragLoadingTimeOut: 20000,
                startFragPrefetch: true,

                // Buffer settings
                maxBufferLength: 30,
                maxMaxBufferLength: 60,
                backBufferLength: 10,
            });

            this.hls.attachMedia(this.video);

            this.hls.on(Hls.Events.MANIFEST_PARSED, () => {
                this.video.play().catch(e => console.warn("Autoplay blocked", e));
            });

            this.hls.on(Hls.Events.FRAG_CHANGED, (event, data) => {
                if (data.frag && data.frag.programDateTime) {
                    this.currentFragPDT = data.frag.programDateTime;
                    this.currentFragStartPTS = data.frag.start;
                }
            });

            this.hls.on(Hls.Events.ERROR, (event, data) => {
                if (data.fatal) {
                    console.error(`[HLS] FATAL: ${data.type}`, data.details);
                    switch (data.type) {
                        case Hls.ErrorTypes.NETWORK_ERROR:
                            console.log('[HLS] Network error, retrying...');
                            this.hls.startLoad();
                            break;
                        case Hls.ErrorTypes.MEDIA_ERROR:
                            console.log('[HLS] Media error, recovering...');
                            this.hls.recoverMediaError();
                            break;
                        default:
                            console.error('[HLS] Unrecoverable error');
                            this.hls.destroy();
                            this.hls = null;
                            break;
                    }
                }
            });
        }

        // Load source ONCE - HLS.js will handle polling automatically
        if (this.hls) {
            this.hls.loadSource(playlistUrl);
        } else if (this.video.canPlayType('application/vnd.apple.mpegurl')) {
            // Safari native HLS
            this.video.src = playlistUrl;
            this.video.addEventListener('loadedmetadata', () => this.video.play());
        }
    }

    seekTo(ts) {
        // For seek, destroy and recreate with new start position
        if (this.hls) {
            this.hls.destroy();
            this.hls = null;
        }
        this.start(ts);
    }

    getCurrentEpochMs() {
        if (this.currentFragPDT && this.hls) {
            const timeInFrag = this.video.currentTime - this.currentFragStartPTS;
            const absoluteTime = this.currentFragPDT + (timeInFrag * 1000);
            return absoluteTime;
        }
        if (this.video.getStartDate && typeof this.video.getStartDate === 'function') {
            const sd = this.video.getStartDate();
            if (sd && !isNaN(sd.getTime())) {
                return sd.getTime() + (this.video.currentTime * 1000);
            }
        }
        return 0;
    }

    destroy() {
        if (this.hls) {
            this.hls.destroy();
            this.hls = null;
        }
    }
}
