import React, { useEffect, useState, useRef } from "react";
import { useSearchParams, useNavigate } from "react-router-dom";
import API from "../api";
import PlaybackCoreV2 from "../services/PlaybackCoreV2";

export default function PlaybackModern() {
    const [searchParams] = useSearchParams();
    const navigate = useNavigate();
    const camId = searchParams.get("camId");

    const [selectedDate, setSelectedDate] = useState("");
    const [timeSegments, setTimeSegments] = useState([]);
    const [currentTimeMs, setCurrentTimeMs] = useState(0);
    const [isPlaying, setIsPlaying] = useState(false);
    const [videoZoom, setVideoZoom] = useState(1);
    const [timelineZoom, setTimelineZoom] = useState(1);
    const [timelineCenterMs, setTimelineCenterMs] = useState(0);
    const [isRefreshing, setIsRefreshing] = useState(false);
    const [dayStartMs, setDayStartMs] = useState(0);

    const videoRef = useRef(null);
    const coreRef = useRef(null);

    // Initial Date
    useEffect(() => {
        const today = new Date().toISOString().split('T')[0];
        setSelectedDate(today);
    }, []);

    // Load Timeline Data
    const loadRecordings = async () => {
        if (!camId || !selectedDate) return;
        setIsRefreshing(true);
        try {
            const res = await API.get(`playback/timeline-day/${camId}/${selectedDate}`);
            const data = res.data; // { dayStart, segments: [{start_ts, end_ts, file}, ...] }

            setDayStartMs(data.dayStart);
            setTimeSegments(data.segments || []);

            if (coreRef.current) {
                coreRef.current.setSegments(data.segments.map(s => ({
                    startTs: s.start_ts,
                    endTs: s.end_ts,
                    file: s.file
                })));
            }

            // Set initial center if not set
            if (timelineCenterMs === 0) {
                setTimelineCenterMs(data.dayStart + (12 * 3600 * 1000)); // Start at noon
            }
        } catch (err) {
            console.error("[Playback] Load failed:", err);
            setTimeSegments([]);
        } finally {
            setIsRefreshing(false);
        }
    };

    useEffect(() => {
        loadRecordings();
    }, [camId, selectedDate]);

    // Initialize/Destroy Playback Core
    useEffect(() => {
        if (videoRef.current && camId) {
            coreRef.current = new PlaybackCoreV2(videoRef.current, camId);
        }
        return () => {
            if (coreRef.current) coreRef.current.destroy();
        };
    }, [camId]);

    // Synchronize Current Time from Video
    useEffect(() => {
        const interval = setInterval(() => {
            if (coreRef.current && isPlaying) {
                const epoch = coreRef.current.getCurrentEpochMs();
                if (epoch) setCurrentTimeMs(epoch);
            }
        }, 100);
        return () => clearInterval(interval);
    }, [isPlaying]);

    const handleSeek = (epochMs) => {
        if (!coreRef.current) return;
        coreRef.current.seekTo(epochMs);
        setCurrentTimeMs(epochMs);
        setTimelineCenterMs(epochMs);
        setIsPlaying(true);
    };

    const togglePlay = () => {
        if (!videoRef.current) return;
        if (videoRef.current.paused) {
            videoRef.current.play();
            setIsPlaying(true);
        } else {
            videoRef.current.pause();
            setIsPlaying(false);
        }
    };

    // Zoom handlers
    const handleVideoWheel = (e) => {
        e.preventDefault();
        setVideoZoom(prev => Math.max(0.5, Math.min(5, prev + (e.deltaY > 0 ? -0.1 : 0.1))));
    };

    const handleTimelineWheel = (e) => {
        e.preventDefault();
        setTimelineZoom(prev => Math.max(0.5, Math.min(100, prev + (e.deltaY > 0 ? -0.5 : 0.5))));
    };

    // Timeline render
    const renderTimeline = () => {
        const width = 1200;
        const viewDurationMs = (24 * 3600 * 1000) / timelineZoom;
        const viewStartMs = timelineCenterMs - viewDurationMs / 2;
        const viewEndMs = timelineCenterMs + viewDurationMs / 2;
        const msPerPixel = viewDurationMs / width;

        const getX = (ts) => (ts - viewStartMs) / msPerPixel;

        return (
            <svg
                width="100%"
                height={100}
                style={{ background: "#0c0c0c", cursor: "crosshair" }}
                viewBox={`0 0 ${width} 100`}
                onClick={(e) => {
                    const rect = e.currentTarget.getBoundingClientRect();
                    const x = e.clientX - rect.left;
                    const seekMs = viewStartMs + (x * (width / rect.width)) * msPerPixel;
                    handleSeek(seekMs);
                }}
            >
                {/* Grid - Hours */}
                {Array.from({ length: 25 }).map((_, i) => {
                    const hourStart = dayStartMs + (i * 3600 * 1000);
                    const x = getX(hourStart);
                    if (x < -50 || x > width + 50) return null;
                    return (
                        <g key={i}>
                            <line x1={x} y1={0} x2={x} y2={100} stroke="#333" strokeOpacity={0.5} />
                            <text x={x + 2} y={15} fill="#555" fontSize={10}>{i}:00</text>
                        </g>
                    );
                })}

                {/* Recordings - GREEN BARS */}
                {timeSegments.map((seg, idx) => {
                    if (seg.end_ts < viewStartMs || seg.start_ts > viewEndMs) return null;
                    const x = Math.max(0, getX(seg.start_ts));
                    const w = Math.max(1, (seg.end_ts - seg.start_ts) / msPerPixel);
                    return (
                        <rect
                            key={idx}
                            x={x}
                            y={35}
                            width={w}
                            height={30}
                            fill="#00ff00"
                            fillOpacity={0.6}
                        />
                    );
                })}

                {/* Current time indicator */}
                {currentTimeMs >= viewStartMs && currentTimeMs <= viewEndMs && (
                    <line
                        x1={getX(currentTimeMs)}
                        y1={0}
                        x2={getX(currentTimeMs)}
                        y2={100}
                        stroke="#ff0000"
                        strokeWidth={2}
                    />
                )}

                <text x={10} y={90} fill="#00ff00" fontSize={11} fontWeight="bold">
                    {timeSegments.length} segments found
                </text>
            </svg>
        );
    };

    const formatFullTime = (ms) => {
        if (!ms) return "--:--:--";
        return new Date(ms).toLocaleTimeString('en-GB');
    };

    return (
        <div style={{ display: "flex", flexDirection: "column", height: "100vh", background: "#050505", color: "#e0e0e0", fontFamily: "Segoe UI, Tahoma, Geneva, Verdana, sans-serif" }}>
            {/* Optimized Header (Enterprise Look) */}
            <div style={{ height: 50, background: "#111", display: "flex", alignItems: "center", padding: "0 20px", borderBottom: "1px solid #222" }}>
                <button onClick={() => navigate("/")} style={{ background: "none", border: "none", color: "#666", fontSize: 20, cursor: "pointer", marginRight: 15 }}>◀</button>
                <div style={{ flex: 1, display: "flex", alignItems: "center", gap: 10 }}>
                    <span style={{ color: "#00ff00", fontWeight: "bold" }}>● ARCHIVE</span>
                    <span style={{ color: "#888" }}>|</span>
                    <span style={{ fontSize: 14 }}>Camera: {camId}</span>
                </div>

                <div style={{ display: "flex", gap: 10, alignItems: "center" }}>
                    <input
                        type="date"
                        value={selectedDate}
                        onChange={(e) => setSelectedDate(e.target.value)}
                        style={{ background: "#1a1a1a", border: "1px solid #333", color: "#fff", padding: "5px 10px", borderRadius: 3, fontSize: 13 }}
                    />
                    <button
                        onClick={loadRecordings}
                        style={{ background: "#222", border: "1px solid #333", color: "#fff", padding: "5px 15px", borderRadius: 3, cursor: "pointer", fontSize: 13 }}
                    >
                        {isRefreshing ? "..." : "Sync"}
                    </button>
                </div>
            </div>

            {/* Main Area */}
            <div style={{ flex: 1, display: "flex", overflow: "hidden" }}>
                {/* Video Container */}
                <div
                    style={{ flex: 1, background: "#000", position: "relative", display: "flex", alignItems: "center", justifyContent: "center" }}
                    onWheel={handleVideoWheel}
                >
                    <video
                        ref={videoRef}
                        onPlay={() => setIsPlaying(true)}
                        onPause={() => setIsPlaying(false)}
                        style={{
                            maxWidth: "100%",
                            maxHeight: "100%",
                            transform: `scale(${videoZoom})`,
                            boxShadow: "0 0 50px rgba(0,0,0,0.5)"
                        }}
                    />

                    {/* Floating Info */}
                    <div style={{ position: "absolute", top: 20, left: 20, background: "rgba(0,0,0,0.6)", padding: "10px", borderRadius: 5, borderLeft: "3px solid #00ff00", backdropFilter: "blur(5px)" }}>
                        <div style={{ fontSize: 24, fontWeight: "bold", fontFamily: "monospace", color: "#00ff00" }}>
                            {formatFullTime(currentTimeMs)}
                        </div>
                        <div style={{ fontSize: 11, color: "#888" }}>{selectedDate}</div>
                    </div>

                    <div style={{ position: "absolute", bottom: 20, right: 20, display: "flex", gap: 10 }}>
                        <div style={{ background: "rgba(0,0,0,0.6)", padding: "5px 10px", borderRadius: 3, fontSize: 11, color: "#aaa" }}>
                            ZOOM: {Math.round(videoZoom * 100)}%
                        </div>
                    </div>
                </div>

                {/* Modern Side Controls */}
                <div style={{ width: 80, background: "#0a0a0a", borderLeft: "1px solid #1a1a1a", display: "flex", flexDirection: "column", alignItems: "center", padding: "20px 0", gap: 20 }}>
                    <button
                        onClick={togglePlay}
                        style={{ width: 50, height: 50, borderRadius: "50%", background: isPlaying ? "#333" : "#00ff00", border: "none", cursor: "pointer", color: isPlaying ? "#fff" : "#000", fontSize: 20 }}
                    >
                        {isPlaying ? "⏸" : "▶"}
                    </button>

                    <div style={{ width: 1, height: 40, background: "#222" }} />

                    <button onClick={() => setTimelineZoom(prev => prev * 2)} style={ctrlBtnStyle}>+</button>
                    <button onClick={() => setTimelineZoom(prev => prev / 2)} style={ctrlBtnStyle}>-</button>

                    <div style={{ flex: 1 }} />

                    <button onClick={() => handleSeek(currentTimeMs - 10000)} style={ctrlBtnStyle}>-10s</button>
                    <button onClick={() => handleSeek(currentTimeMs + 10000)} style={ctrlBtnStyle}>+10s</button>
                </div>
            </div>

            {/* Timeline Panel */}
            <div
                style={{ height: 120, background: "#080808", borderTop: "1px solid #1a1a1a" }}
                onWheel={handleTimelineWheel}
            >
                {renderTimeline()}
            </div>
        </div>
    );
}

const ctrlBtnStyle = {
    width: 40,
    height: 40,
    background: "#151515",
    border: "1px solid #222",
    color: "#fff",
    borderRadius: 5,
    cursor: "pointer",
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
    fontSize: 14,
    transition: "all 0.2s"
};
