/**
 * DSS – canonical UTC timestamp converter
 *
 * ACCEPTĂ:
 *  - string: "2026-01-12" + "14:00"
 *  - number: millis (assumed local → converted)
 *  - Date object (local → converted)
 *
 * RETURN:
 *  - UTC timestamp (ms)
 */

export function toUtcTs(input, time = null) {

    // Case 1: date + time strings
    if (typeof input === 'string' && time) {
        // e.g. input="2026-01-12", time="14:00" => UTC is T..:00Z
        return Date.parse(`${input}T${time}:00Z`);
    }

    // Case 2: ISO string already UTC
    if (typeof input === 'string' && input.endsWith('Z')) {
        return Date.parse(input);
    }

    // Case 3: millis (local)
    if (typeof input === 'number') {
        const d = new Date(input);
        return Date.UTC(
            d.getFullYear(),
            d.getMonth(),
            d.getDate(),
            d.getHours(),
            d.getMinutes(),
            d.getSeconds(),
            d.getMilliseconds()
        );
    }

    // Case 4: Date object (local)
    if (input instanceof Date) {
        return Date.UTC(
            input.getFullYear(),
            input.getMonth(),
            input.getDate(),
            input.getHours(),
            input.getMinutes(),
            input.getSeconds(),
            input.getMilliseconds()
        );
    }

    throw new Error('toUtcTs: unsupported input');
}

export function fromUtcTs(ts) {
    return new Date(ts); // DOAR UI
}
